import { validateOptions } from "../../Utils/UserValidation.js";
import DB from "../../schemas/LevelDB.js";
import { validateXP } from "../../Utils/XpValidation.js";
export class XP {
    #options;
    /**
     * Initialise a new XP instance
     * @param {{ guildId: string, userId: string }} options
     */
    constructor(options) {
        this.#options = options;
        const validate = validateOptions(this.#options);
        if (validate.invalid)
            throw new TypeError(validate.error);
    }
    /**
     * Add XP to the user
     * @param {number} amount
     * @returns {Promise<object | null>}
     */
    add(amount = 1) {
        return new Promise(async (res, rej) => {
            const XPValidation = validateXP(amount);
            if (XPValidation.invalid)
                return rej(XPValidation.error);
            const data = await DB.findOne({ guildId: this.#options.guildId, userId: this.#options.userId });
            if (!data)
                return res(null);
            data.xp += amount;
            await data.save();
            return res(data);
        });
    }
    /**
     * Subtract XP from the user
     * @param {number} amount
     * @returns {Promise<object | null>}
     */
    subtract(amount = 1) {
        return new Promise(async (res, rej) => {
            const XPValidation = validateXP(amount);
            if (XPValidation.invalid)
                return rej(XPValidation.error);
            const data = await DB.findOne({ guildId: this.#options.guildId, userId: this.#options.userId });
            if (!data)
                return res(null);
            data.xp -= amount;
            await data.save();
            return res(data);
        });
    }
    /**
     * Set the XP of the user
     * @param {number} amount
     * @returns {Promise<object | null>}
     */
    set(amount = 1) {
        return new Promise(async (res, rej) => {
            const XPValidation = validateXP(amount);
            if (XPValidation.invalid)
                return rej(XPValidation.error);
            const data = await DB.findOne({ guildId: this.#options.guildId, userId: this.#options.userId });
            if (!data)
                return res(null);
            data.xp = amount;
            await data.save();
            return res(data);
        });
    }
}
