import DB from "../schemas/LevelDB.js";
/**
 * Checks to make sure that the correct options have been given
 * @param {UserOptions} options
 * @returns {{ invalid: boolean, error?: string }}
 */
export function validateOptions(options) {
    if (!options.guildId)
        return ({ invalid: true, error: "A guild ID was not provided" });
    if (!options.userId)
        return ({ invalid: true, error: "A user ID was not provided" });
    return ({ invalid: false });
}
/**
 * Checks if a schema exists
 * @param {UserOptions} options
 * @returns {Promise<boolean>}
 */
export async function schemaExists(options) {
    const data = await DB.findOne({ guildId: options.guildId, userId: options.userId });
    return data ? true : false;
}
